export default function ReportRendered({
  onClose,
  onPrint,
}: {
  onClose?: () => void;
  onPrint?: () => void;
}) {
  const [open, setOpen] = useState(false);
  const renderMainAreaButtons = () => {
    return options.map((option) => {
      return (
        <button
          className={styles.labelItem}
          style={{ marginBottom: 24 }}
          onClick={() => {}}
        >
          {option}
        </button>
      );
    });
  };
  return (
    <div>
      <div style={{ display: "flex", flexDirection: "row", gap: 12 }}>
        {renderMainAreaButtons()}
      </div>
      <button onClick={() => setOpen(true)}>Open</button>
      <FullScreenModal open={open} onClose={onClose}>
        <div
          style={{
            height: "100%",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          {/* Sidebar-style label button */}
          <button
            className={styles.labelItem}
            style={{ width: 200, marginBottom: 24 }}
            onClick={() => {}}
          >
            Sidebar Style
          </button>
          <div style={{ padding: "2rem" }}>
            <ImageScroller
              src={bbcode}
              // templateData={{ user: "Maria", coins: 172 }}
              /* stretch horizontally */
              fill={true}
              sourceType="html"
              className="full-width-scroller"
            />
          </div>
          <div
            style={{
              flex: "0 0 10%",
              width: "100%",
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              padding: "0 2rem",
            }}
          >
            <button
              style={{
                background: "red",
                color: "#fff",
                border: "none",
                borderRadius: 6,
                padding: "12px 32px",
                fontWeight: 600,
                fontSize: 18,
                cursor: "pointer",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
              }}
              onClick={onClose || (() => setOpen(false))}
            >
              <X size={28} style={{ marginBottom: 4 }} />
              Close
            </button>
            <button
              style={{
                background: "#2563eb",
                color: "#fff",
                border: "none",
                borderRadius: 6,
                padding: "12px 32px",
                fontWeight: 600,
                fontSize: 18,
                cursor: "pointer",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
              }}
              onClick={onPrint || (() => alert("Print"))}
            >
              <Printer size={28} style={{ marginBottom: 4 }} />
              Print
            </button>
          </div>
        </div>
      </FullScreenModal>
    </div>
  );
}
